    //   }
    //   fetchProduct()
    },[])
//let product = {}
  return (
    <div>
     <Link to ='/' className='btn btn-light my-3'>Go Back</Link>
    {
        loading ? 
        <Loader />
        : error
        ?<Message var
        variant='danger'>{error}</Message>
        :(
            
nughtam
        )
    }
     <Row>
        <Col md = {6}>
            <Image src={product.image} alt={product.name} fluid/>
        </Col>
        <Col md = {3}>
            <ListGroup varient='flush'>
                <ListGroup.Item>
                    <h3>{product.name}</h3>
                </ListGroup.Item>
                <ListGroup.Item>
                    <Rating value={product.rating} text= {`${product.numReviews} reviews `} color={`#f8e825`}/>
                </ListGroup.Item>

                <ListGroup.Item>
                   Price : ${product.price}
                </ListGroup.Item>
                <ListGroup.Item>
                   Decription : {product.description }
                </ListGroup.Item>
               

            </ListGroup>
        </Col>
        <Col md = {3}>
            <Card>
                <ListGroup variant = 'flush'>
                    <ListGroup.Item>
                        <Row>
                            <Col>Price:</Col>
                            <Col>
                            <strong>${product.price}</strong>
                            </Col>

                        </Row>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Row>
                            <Col>Status:</Col>
                            <Col>
                           {product.countInStock > 0 ? 'In Stock' : 'Out of Stock' }
                            </Col>

                        </Row>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Button className = 'btn-block' disabled={product.countInStock==0} type = 'button'>
                        Add To Cart
                        </Button>
                    </ListGroup.Item>
                    <ListGroup/>

                </ListGroup>
            </Card>
        </Col>
     </Row>
    </div>
  )
}

export default ProductScreen;
